"use strict";

var BTN_PLAY = document.getElementById("button-play"),
  POPUP_CONTAINER = document.getElementById("popup-container"),
  POPUP_VIDEO = document.getElementById("popup-video"),
  FIND_BTN = document.getElementById("find-out-button"),
  PRODUCTS_BLOCK = document.getElementById("products"),
  BURGER_BTN = document.getElementById("burger-button"),
  MENU_BLOCK = document.querySelector(".menu");
function popupOpenClose(e) {
  "button-play" === e.target.id && POPUP_CONTAINER.classList.toggle("display_none"), "button-close" !== e.target.id && "popup-container" !== e.target.id || POPUP_CONTAINER.classList.toggle("display_none");
}
function smoothScroll(e) {
  window.scrollTo({
    block: "center",
    top: e.offsetTop,
    behavior: "smooth"
  });
}
function menuItemClick() {
  MENU_BLOCK.classList.remove("display_flex"), MENU_BLOCK.removeEventListener("click", menuItemClick);
}
document.addEventListener("click", popupOpenClose), BURGER_BTN.addEventListener("click", function () {
  MENU_BLOCK.classList.toggle("display_flex"), MENU_BLOCK.addEventListener("click", menuItemClick);
});